pragma solidity ^0.5.11;

import "../zeppelin//MerkleProof.sol";
import "../ManagerProxyTarget.sol";


contract MerkleSnapshot is ManagerProxyTarget{
    mapping (bytes32 => bytes32) public snapshot;

    constructor(address _controller) public Manager(_controller) {}

    function setSnapshot(bytes32 _id, bytes32 _root) external onlyControllerOwner {
        require(snapshot[_id] == bytes32(0), "Snapshot root for ID already exists");
        snapshot[_id] = _root;
    }

    function verify(bytes32 _id, bytes32[] calldata _proof, bytes32 _leaf) external view returns (bool) {
        require(
            MerkleProof.verify(_proof, snapshot[_id], _leaf),
            "Merkle proof for the provided leaf is invalid"
        );

        return true;
    }
}
